$('form[name="OPEN_DOOR"] input[type="submit"], form[name="DOCUMENTS_Button"] input[type="submit"]').on('click', function(event){
	if ($('.checkbox input[name="rules"]').is(':checked')) {
		$('.checkbox a').removeClass('reddy')
	}else{
		$('.checkbox a').addClass('reddy')
		return false;
	}
})


var isClick = true;

 var yandexSendWebForm = function(event) {
	console.log('okay')
 		if(isClick){
 			event.preventDefault()
 			var _this = this;
	        var yandexPromise = new Promise(function(resolve, reject) {
	            if (window.smartCaptcha) {
	                if(typeof window.smartCaptchaObjId !== 'undefined' && Object.keys(window.smartCaptchaObjId).length > 0){
	                    var blockId = $(_this).find('.captcha-container').attr('id');
	                    if(typeof window.smartCaptchaObjId[blockId] !== 'undefined'){
	                        window.smartCaptcha.execute(window.smartCaptchaObjId[blockId])  
	                    }else{
	                        window.smartCaptcha.execute()
	                    }
	                }else{
	                    window.smartCaptcha.execute()   
	                }

	            }else{
	                resolve(true); 
	            }

	            document.addEventListener('InitYandexCaptcha', e => {
	                resolve(true); 
	            });
	        });

	        yandexPromise.then(result => {
	        	isClick = false;
	        	$(this).find('input[type="submit"]').trigger('click');
	  		});
 		}else{
 			isClick = true;
 		}   
}

$(document).on('submit', 'form[name="OPEN_DOOR"]', yandexSendWebForm);
$(document).on('submit', 'form[name="DOCUMENTS"]', yandexSendWebForm);
$(document).on('submit', 'form[name="DOCUMENTS_Button"]', yandexSendWebForm);

