						catch(e)
						{
							var errorTr = BX("bxph_error_row");
							if (errorTr)
							{
								errorTr.style.display = "";
								errorTr.cells[0].innerHTML = _this.MESS.unknownError;
							}
							window.oPhotoEditDialog.adjustSizeEx();
						}

						if (top.result)
