:root {
	--black: #1c1a1a;
	--white: #fff;
	--l-gray: #a3a3a3;
	--gray: #8c8c8c;
	--green: #00fe5d;
	--wgreen: #73ffa7;
	--pink: #ff00ff;
	--wpink: #ff73ff;
	--blue: #293cf4;
	--purple: #b1a7fc;
	--bg-gb: linear-gradient(90deg, rgba(0,254,93,1) 0%, rgba(41,60,244,1) 100%);
	--bg-pp: linear-gradient(90deg, rgba(255, 0, 255,1) 0%, rgba(177, 167, 252,1) 100%);
	--font-size: 1.25rem;
	--line-height: 1.5;
	--border: 3px solid #fff;
	--border-b: 3px solid #1c1a1a;
	--border-sm: 2px solid #fff;
	--border-pink: 3px solid #ff00ff;
	--b-radius-sm: 10px;
	--b-radius: 15px;
	--b-radius-lg: 35px;
	--transition: all .5s cubic-bezier(0, 0.25, 0.5, 1);
	--transform: all .3s linear 0s, transform 0s
}

*,
*::before,
*::after {
	box-sizing: border-box
}

*:focus {
	outline: none
}

@media (prefers-reduced-motion: no-preference) {
:root {
	scroll-behavior: smooth
}
}

.nowrap {
    white-space: nowrap
}

.frame ul,
.frame ol {
    margin-left: 0;
    padding-left: 0
}

.frame li {
    list-style-type: none
}

body {
	margin: 0;
	font-family: 'Gotham Pro', sans-serif;
	font-size: var(--font-size);
	line-height: var(--line-height);
	color: var(--white);
	text-align: var(--text-align);
	background-color: var(--black);
	-webkit-text-size-adjust: 100%;
	-webkit-tap-highlight-color: rgba(0, 0, 0, 0)
}

body.light {
	color: var(--black);
	background-color: transparent
}

blockquote {
	width: 620px;
	max-width: 100%;
	margin: 40px 0;
	padding: 0 20px;
	border-left: 6px solid var(--green);
	color: var(--gray)
}

blockquote.white {
	border-left: 6px solid var(--white)
}

blockquote.gray {
	border-left: 6px solid var(--gray)
}

blockquote.pink {
	border-left: 6px solid var(--pink)
}

blockquote.blue {
	border-left: 6px solid var(--blue)
}

blockquote.purple {
	border-left: 6px solid var(--purple)
}

img {
	max-width: 100%
}

h6, h5, h4, h3, h2, h1 {
	margin: 0 0 1rem;
	font-family: 'Halvar Breitschrift Md', sans-serif;
	font-weight: 500;
	line-height: 1.2
}

h1 {
	font-size: calc(1.375rem + 1.5vw)
}

